import lazy from './lazy'
export default(routeList, routeHideList, menuList)=>{
function toRoutes(routeList, routeHideList, menuList){
	if(menuList){
		let list = [{
		    		path : 'index',
		    		name : 'index',
		    		component : () => import('@/views/index.vue'),
		    		meta: {
				        title: '项目总览'
				    },
				    children:[]
		    	}]
		for(var i=0;i<menuList.length;i++){
			var menuItem = menuList[i];
			if(menuItem.type){
				var routeNode = {
					path : menuItem.path,
		    		name : menuItem.path,
		    		component : lazy(menuItem.path),
		    		meta: {
				        title: menuItem.name
				    },
				    children:[]
				}
				if(menuItem.isShow){
					routeList.push(routeNode)
				}else{
					routeHideList.push(routeNode)
				}
				if(menuItem.children&&menuItem.children.length){
					toRoutes(routeList, routeHideList,menuItem.children)
				}
			}else{
				if(menuItem.children&&menuItem.children.length){
					toRoutes(routeList, routeHideList,menuItem.children)
				}
				
			}
			
		}
	}
}
}
